# Requires pandoc and xelatex.
PANDOC=`which pandoc`
$PANDOC  --output "thesis.pdf" --variable mainfont="Gentium"  --template="typesetting/style/template.tex" --include-in-header="typesetting/style/preamble.tex"                 --toc --variable=fontsize:12pt --variable=documentclass:report --pdf-engine=xelatex "Chapters"/*.md  "Chapters/metadata.yml" --bibliography="Chapters/references.bib" --citeproc --csl="typesetting/style/ref_format.csl"  --number-sections --verbose